import React, { useState, useEffect, useCallback } from 'react';

const WINDOW_SIZE = 50; // Points visible at once
const UPDATE_INTERVAL = 100; // Update interval in ms
const GRAPH_HEIGHT = 400;
const GRAPH_WIDTH = 600;

const videoMapping = {
  0: { name: "Sit", path: "/videos/sit.mp4" },
  1: { name: "Forward Bend", path: "/videos/forward_bend.mp4" },
  2: { name: "Warrior Pose", path: "/videos/warrior.mp4" },
  3: { name: "Triangle Pose", path: "/videos/triangle.mp4" },
  4: { name: "Vajrasana", path: "/videos/vajrasan.mp4" },
  5: { name: "Backward Bend", path: "/videos/backwardbend_pose.mp4" },
};

const CsvGraphPlotter = () => {
  const [data, setData] = useState([]);
  const [currentIndex, setCurrentIndex] = useState(0);
  const [visibleData, setVisibleData] = useState([]);
  const [currentActivity, setCurrentActivity] = useState("Unknown Activity");
  const [videoSrc, setVideoSrc] = useState("");

  // Generate line colors
  const lineColors = Array.from({ length: 52 }, (_, i) => 
    `hsl(${(i * 7) % 360}, 70%, 50%)`
  );

  const parseCSV = (content) => {
    const lines = content.split('\n');
    return lines.map(line => {
      const values = line.split(',');
      if (values.length === 53) {
        return {
          values: values.slice(0, 52).map(v => parseFloat(v) || 0),
          activity: parseInt(values[52], 10)
        };
      }
      return null;
    }).filter(Boolean);
  };

  const handleFileUpload = (event) => {
    const file = event.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onload = (e) => {
        const parsedData = parseCSV(e.target.result);
        setData(parsedData);
        setCurrentIndex(0);
        setVisibleData(parsedData.slice(0, WINDOW_SIZE));
      };
      reader.readAsText(file);
    }
  };

  // Create SVG paths for each data series
  const createPath = (dataPoints, columnIndex) => {
    if (!dataPoints.length) return '';
    
    const xStep = GRAPH_WIDTH / (WINDOW_SIZE - 1);
    const yScale = GRAPH_HEIGHT / 20; // Scale for 0-20 range

    return dataPoints.map((point, i) => {
      const x = i * xStep;
      const y = GRAPH_HEIGHT - (point.values[columnIndex] * yScale);
      return `${i === 0 ? 'M' : 'L'} ${x} ${y}`;
    }).join(' ');
  };

  const updateChartAndVideo = useCallback(() => {
    if (data.length === 0) return;

    const endIndex = currentIndex + WINDOW_SIZE;
    const newVisibleData = data.slice(
      Math.max(0, currentIndex),
      Math.min(endIndex, data.length)
    );

    if (newVisibleData.length > 0) {
      const currentRow = data[currentIndex];
      const label = currentRow.activity;
      
      if (videoMapping[label]) {
        setCurrentActivity(videoMapping[label].name);
        setVideoSrc(videoMapping[label].path);
      } else {
        setCurrentActivity("Unknown Activity");
        setVideoSrc("");
      }
    }

    setVisibleData(newVisibleData);
  }, [data, currentIndex]);

  useEffect(() => {
    if (data.length === 0) return;

    const interval = setInterval(() => {
      setCurrentIndex(prevIndex => {
        const nextIndex = prevIndex + 1;
        return nextIndex >= data.length ? 0 : nextIndex;
      });
    }, UPDATE_INTERVAL);

    return () => clearInterval(interval);
  }, [data]);

  useEffect(() => {
    updateChartAndVideo();
  }, [currentIndex, updateChartAndVideo]);

  return (
    <div className="p-5 text-center">
      <h1 className="text-2xl font-bold mb-4">Real-time CSV Graph Plotter</h1>
      <input
        type="file"
        accept=".csv"
        onChange={handleFileUpload}
        className="mb-6 p-2"
      />

      <div className="flex justify-around items-start gap-4">
        <div className="w-1/2">
          <svg
            width={GRAPH_WIDTH}
            height={GRAPH_HEIGHT}
            className="bg-white border border-gray-300"
          >
            {/* Grid lines */}
            {Array.from({ length: 11 }, (_, i) => (
              <line
                key={`grid-${i}`}
                x1="0"
                y1={i * (GRAPH_HEIGHT / 10)}
                x2={GRAPH_WIDTH}
                y2={i * (GRAPH_HEIGHT / 10)}
                stroke="#eee"
                strokeWidth="1"
              />
            ))}
            
            {/* Data lines */}
            {Array.from({ length: 52 }, (_, i) => (
              <path
                key={`line-${i}`}
                d={createPath(visibleData, i)}
                stroke={lineColors[i]}
                fill="none"
                strokeWidth="1"
              />
            ))}
          </svg>
        </div>

        <div className="w-1/2">
          <h2 className="text-xl font-semibold mb-4">Current Activity: {currentActivity}</h2>
          {videoSrc ? (
            <video
              key={videoSrc}
              className="w-full h-[400px] object-contain pointer-events-none"
              loop
              autoPlay
              muted
              controls={false}
            >
              <source src={videoSrc} type="video/mp4" />
              Your browser does not support the video tag.
            </video>
          ) : (
            <p>No video available for the current activity.</p>
          )}
        </div>
      </div>
    </div>
  );
};

export default CsvGraphPlotter;
