import React, { useState, useEffect, useCallback } from 'react';

const WINDOW_SIZE = 50;
const UPDATE_INTERVAL = 100;
const GRAPH_HEIGHT = 400;
const GRAPH_WIDTH = 600;
const MARGIN = { top: 20, right: 30, bottom: 40, left: 50 };

const videoMapping = {
  0: { name: "Sit", path: "/videos/sit.mp4" },
  1: { name: "Forward Bend", path: "/videos/forward_bend.mp4" },
  2: { name: "Warrior Pose", path: "/videos/warrior.mp4" },
  3: { name: "Triangle Pose", path: "/videos/triangle.mp4" },
  4: { name: "Vajrasana", path: "/videos/vajrasan.mp4" },
  5: { name: "Backward Bend", path: "/videos/backwardbend_pose.mp4" },
};

const CsvGraphPlotter = () => {
  const [data, setData] = useState([]);
  const [currentIndex, setCurrentIndex] = useState(0);
  const [visibleData, setVisibleData] = useState([]);
  const [currentActivity, setCurrentActivity] = useState("Unknown Activity");
  const [videoSrc, setVideoSrc] = useState("");

  const lineColors = Array.from({ length: 52 }, (_, i) => 
    `hsl(${(i * 7) % 360}, 70%, 50%)`
  );

  const parseCSV = (content) => {
    const lines = content.split('\n');
    return lines.map(line => {
      const values = line.split(',');
      if (values.length === 53) {
        return {
          values: values.slice(0, 52).map(v => parseFloat(v) || 0),
          activity: parseInt(values[52], 10)
        };
      }
      return null;
    }).filter(Boolean);
  };

  const handleFileUpload = (event) => {
    const file = event.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onload = (e) => {
        const parsedData = parseCSV(e.target.result);
        setData(parsedData);
        setCurrentIndex(0);
        setVisibleData(parsedData.slice(0, WINDOW_SIZE));
      };
      reader.readAsText(file);
    }
  };

  const createPath = (dataPoints, columnIndex) => {
    if (!dataPoints.length) return '';
    
    const xStep = (GRAPH_WIDTH - MARGIN.left - MARGIN.right) / (WINDOW_SIZE - 1);
    const yScale = (GRAPH_HEIGHT - MARGIN.top - MARGIN.bottom) / 20;

    return dataPoints.map((point, i) => {
      const x = MARGIN.left + (i * xStep);
      const y = GRAPH_HEIGHT - MARGIN.bottom - (point.values[columnIndex] * yScale);
      return `${i === 0 ? 'M' : 'L'} ${x} ${y}`;
    }).join(' ');
  };

  const updateChartAndVideo = useCallback(() => {
    if (data.length === 0) return;

    const endIndex = currentIndex + WINDOW_SIZE;
    const newVisibleData = data.slice(
      Math.max(0, currentIndex),
      Math.min(endIndex, data.length)
    );

    if (newVisibleData.length > 0) {
      const currentRow = data[currentIndex];
      const label = currentRow.activity;
      
      if (videoMapping[label]) {
        setCurrentActivity(videoMapping[label].name);
        setVideoSrc(videoMapping[label].path);
      } else {
        setCurrentActivity("Unknown Activity");
        setVideoSrc("");
      }
    }

    setVisibleData(newVisibleData);
  }, [data, currentIndex]);

  useEffect(() => {
    if (data.length === 0) return;

    const interval = setInterval(() => {
      setCurrentIndex(prevIndex => {
        const nextIndex = prevIndex + 1;
        return nextIndex >= data.length ? 0 : nextIndex;
      });
    }, UPDATE_INTERVAL);

    return () => clearInterval(interval);
  }, [data]);

  useEffect(() => {
    updateChartAndVideo();
  }, [currentIndex, updateChartAndVideo]);

  // Generate Y-axis ticks
  const yAxisTicks = Array.from({ length: 11 }, (_, i) => ({
    value: i * 2,
    y: GRAPH_HEIGHT - MARGIN.bottom - (i * (GRAPH_HEIGHT - MARGIN.top - MARGIN.bottom) / 10)
  }));

  return (
    <div className="p-5">
      <div className="max-w-7xl mx-auto">
        <h1 className="text-2xl font-bold mb-4 text-center">Real-time Sensor Data Visualization</h1>
        
        <div className="mb-6 text-center">
          <input
            type="file"
            accept=".csv"
            onChange={handleFileUpload}
            className="p-2 border border-gray-300 rounded"
          />
        </div>

        <div className="flex items-start gap-8">
          <div className="flex-1">
            <div className="relative bg-white rounded-lg shadow-md p-4">
              <h2 className="text-lg font-semibold mb-2">Current Activity: {currentActivity}</h2>
              <svg
                width={GRAPH_WIDTH}
                height={GRAPH_HEIGHT}
                className="bg-white"
              >
                {/* Y-axis line */}
                <line
                  x1={MARGIN.left}
                  y1={MARGIN.top}
                  x2={MARGIN.left}
                  y2={GRAPH_HEIGHT - MARGIN.bottom}
                  stroke="#666"
                  strokeWidth="1"
                />
                
                {/* X-axis line */}
                <line
                  x1={MARGIN.left}
                  y1={GRAPH_HEIGHT - MARGIN.bottom}
                  x2={GRAPH_WIDTH - MARGIN.right}
                  y2={GRAPH_HEIGHT - MARGIN.bottom}
                  stroke="#666"
                  strokeWidth="1"
                />

                {/* Grid lines and Y-axis ticks */}
                {yAxisTicks.map(({ value, y }) => (
                  <g key={`tick-${value}`}>
                    <line
                      x1={MARGIN.left}
                      y1={y}
                      x2={GRAPH_WIDTH - MARGIN.right}
                      y2={y}
                      stroke="#eee"
                      strokeWidth="1"
                    />
                    <text
                      x={MARGIN.left - 10}
                      y={y}
                      textAnchor="end"
                      alignmentBaseline="middle"
                      className="text-xs"
                    >
                      {value}
                    </text>
                  </g>
                ))}

                {/* Axis labels */}
                <text
                  x={GRAPH_WIDTH / 2}
                  y={GRAPH_HEIGHT - 5}
                  textAnchor="middle"
                  className="text-sm font-medium"
                >
                  Time (samples)
                </text>
                <text
                  x={-GRAPH_HEIGHT / 2}
                  y={15}
                  textAnchor="middle"
                  transform={`rotate(-90)`}
                  className="text-sm font-medium"
                >
                  Sensor Values
                </text>

                {/* Data lines */}
                {Array.from({ length: 52 }, (_, i) => (
                  <path
                    key={`line-${i}`}
                    d={createPath(visibleData, i)}
                    stroke={lineColors[i]}
                    fill="none"
                    strokeWidth="1.5"
                  />
                ))}
              </svg>
            </div>
          </div>

          <div className="w-96">
            {videoSrc ? (
              <video
                key={videoSrc}
                className="w-full rounded-lg shadow-md"
                loop
                autoPlay
                muted
                controls={false}
              >
                <source src={videoSrc} type="video/mp4" />
                Your browser does not support the video tag.
              </video>
            ) : (
              <div className="h-[400px] flex items-center justify-center bg-gray-100 rounded-lg">
                <p className="text-gray-500">No video available</p>
              </div>
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

export default CsvGraphPlotter;
