import React from "react";
import { Line } from "react-chartjs-2";

const GraphBlock = ({ data }) => {
  const labels = Array.from({ length: data.length }, (_, i) => `Row ${i + 1}`);

  const datasets = Array.from({ length: 52 }, (_, colIndex) => ({
    label: `Amplitude ${colIndex + 1}`,
    data: data.map((row) => parseFloat(row[colIndex])),
    borderColor: `rgba(${(colIndex * 5) % 255}, ${(colIndex * 10) % 255}, ${(colIndex * 15) % 255}, 0.8)`,
    fill: false,
  }));

  const chartData = {
    labels,
    datasets,
  };

  return (
    <div className="graph-block">
      <h2>Amplitude Graph</h2>
      <Line data={chartData} />
    </div>
  );
};

export default GraphBlock;
