import React, { useState, useEffect } from "react";
import Papa from "papaparse";
import { Line } from "react-chartjs-2";
import "chart.js/auto";
// import labelVideoMapping from "./labelVideoMapping"; 



const videoMapping = {
    0: { name: "Sit", path: "/videos/sit.mp4" },
    1: { name: "Forward Bend", path: "/videos/forward_bend.mp4" },
    2: { name: "Warrior Pose", path: "/videos/warrior.mp4" },
    3: { name: "Triangle Pose", path: "/videos/triangle.mp4" },
    4: { name: "Vajrasana", path: "/videos/vajrasan.mp4" },
    5: { name: "Backward Bend", path: "/videos/backwardbend_pose.mp4" },
  };

const CsvGraphPlotter = () => {
  const [data, setData] = useState([]); 
  const [currentIndex, setCurrentIndex] = useState(0); 
  const [chartData, setChartData] = useState(null);
  const [activity, setActivity] = useState("Unknown Activity"); // Store current activity name
  const [videoSrc, setVideoSrc] = useState(""); // Store the current video source

  useEffect(() => {
    if (data.length > 0) {
      const interval = setInterval(() => {
        updateChart(currentIndex);
        setCurrentIndex((prevIndex) => (prevIndex + 50) % data.length);
      }, 1000); // Update every 1 second
      return () => clearInterval(interval); // Clear interval when component unmounts
    }
  }, [data, currentIndex]);

  // Handle CSV file upload
  const handleFileUpload = (event) => {
    const file = event.target.files[0];
    if (file) {
      Papa.parse(file, {
        complete: (result) => {
          const parsedData = result.data.filter((row) => row.length === 53); 
          console.log("Parsed Data:", parsedData); 
          setData(parsedData); 
          updateChart(0); // Start charting with the first 50 rows
        },
        header: false,
        skipEmptyLines: true,
      });
    }
  };

  
  const updateChart = (startIndex) => {
    const endIndex = startIndex + 50 > data.length ? data.length : startIndex + 50;
    const rows = data.slice(startIndex, endIndex); // Get the next chunk of rows
    const xLabels = rows.map((_, idx) => `t ${startIndex + idx + 1}`); // Labels for rows (x-axis)
  
    
    console.log("Rows being processed:", rows);
  
    // Create datasets for each column from 0 to 51 (52 columns in total)
    const datasets = Array.from({ length: 52 }, (_, colIdx) => {
      const columnData = rows.map((row) => parseFloat(row[colIdx])); 
      console.log(`Column ${colIdx + 1} data:`, columnData);
  
      return {
        label: `Column ${colIdx + 1}`, // Label for each column (1 to 52)
        data: columnData, // Ensure that data for the column is properly included
        borderColor: `rgba(${Math.random() * 255}, ${Math.random() * 255}, ${Math.random() * 255}, 0.6)`, // Random colors for each column
        borderWidth: 1,
        fill: false,
      };
    });
  
    setChartData({
      labels: xLabels, // Set x-axis labels
      datasets,
    });
  
    // Access the 53rd column for the activity label (index 52)
    const firstRow = rows[0];
    if (firstRow && firstRow.length > 52) {
      const label = parseInt(firstRow[52], 10); // 53rd column (index 52), ensure it's an integer
  
      // Ensure the label is valid and exists in the mapping
      if (label >= 0 && label <= 5 && labelVideoMapping[label]) {
        const { name, video } = labelVideoMapping[label];  // Get activity name and video path
        setActivity(name); 
        setVideoSrc(video);
        console.log(`Activity: ${name}, Video: ${video}`); 
      } else {
        setActivity("Unknown Activity");
        setVideoSrc(""); 
        console.log("Unknown Activity or invalid label.");
      }
    } else {
      setActivity("Unknown Activity");
      setVideoSrc(""); 
      console.log("Label column not found.");
    }
  };
  

  return (
    <div style={{ textAlign: "center", padding: "20px" }}>
      <h1>CSV File Graph Plotter</h1>
      {/* File Upload */}
      <input
        type="file"
        accept=".csv"
        onChange={handleFileUpload}
        style={{ margin: "20px", padding: "10px" }}
      />

      {/* Split View: Graph and Video */}
      <div style={{ display: "flex", justifyContent: "space-around", padding: "20px" }}>
        {/* Graph Section */}
        <div style={{ width: "45%" }}>
          {chartData ? (
            <Line
            data={chartData}
            options={{
              responsive: true,
              maintainAspectRatio: false,
              scales: {
                x: {
                  title: { display: true, text: "Rows", font: { size: 14 } },
                  grid: { display: true },
                },
                y: {
                  title: { display: true, text: "Values", font: { size: 14 } },
                  grid: { display: true },
                  min: 0,
                  max: 20, 
                  ticks: {
                    stepSize: 2, // Step size to control spacing between ticks (optional)
                    callback: (value) => value.toFixed(2), // Optionally format the y-axis tick labels
                  },
                },
              },
              plugins: {
                legend: { display: false }, 
                tooltip: { mode: "index", intersect: false },
              },
            }}
          />
          
          ) : (
            <p style={{ color: "#888" }}>Upload a CSV file to display the graph.</p>
          )}
        </div>

        {/* Video Section */}
        <div style={{ width: "45%" }}>
          <h2>Current Activity: {activity}</h2>
          {videoSrc ? (
            <video width="100%" height="400" controls loop>
              <source src={videoSrc} type="video/mp4" />
              Your browser does not support the video tag.
            </video>
          ) : (
            <p>No video available for the current activity.</p>
          )}
        </div>
      </div>
    </div>
  );
};

export default CsvGraphPlotter;
