import React, { useState, useEffect } from "react";
// import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Line } from "react-chartjs-2";
import "chart.js/auto";

const videoMapping = {
  0: { name: "Sit", path: "/videos/sit.mp4" },
  1: { name: "Forward Bend", path: "/videos/forward_bend.mp4" },
  2: { name: "Warrior Pose", path: "/videos/warrior.mp4" },
  3: { name: "Triangle Pose", path: "/videos/triangle.mp4" },
  4: { name: "Vajrasana", path: "/videos/vajrasan.mp4" },
  5: { name: "Backward Bend", path: "/videos/backwardbend_pose.mp4" },
};

const CsvGraphPlotter = () => {
  const [data, setData] = useState([]);
  const [currentIndex, setCurrentIndex] = useState(0);
  const [chartData, setChartData] = useState({
    labels: [],
    datasets: Array.from({ length: 52 }, (_, colIdx) => ({
      label: `Sensor ${colIdx + 1}`,
      data: [],
      borderColor: `hsl(${(colIdx * 7) % 360}, 70%, 50%)`,
      borderWidth: 1.5,
      fill: false,
    })),
  });
  const [currentActivity, setCurrentActivity] = useState("Unknown Activity");
  const [videoSrc, setVideoSrc] = useState("");

  useEffect(() => {
    if (data.length > 0) {
      const interval = setInterval(() => {
        updateChartAndVideo();
        setCurrentIndex((prevIndex) => (prevIndex + 1) % data.length);
      }, 200);
      return () => clearInterval(interval);
    }
  }, [data, currentIndex]);

  const handleFileUpload = (event) => {
    const file = event.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onload = (e) => {
        const content = e.target.result;
        const lines = content.split('\n');
        const parsedData = lines
          .map(line => line.split(','))
          .filter(row => row.length === 53);
        setData(parsedData);
      };
      reader.readAsText(file);
    }
  };

  const updateChartAndVideo = () => {
    const currentRow = data[currentIndex];
    if (!currentRow) return;

    setChartData((prevData) => {
      const newLabels = [...prevData.labels, `t ${currentIndex + 1}`];
      if (newLabels.length > 50) newLabels.shift();

      const newDatasets = prevData.datasets.map((dataset, idx) => {
        const newData = [...dataset.data, parseFloat(currentRow[idx])];
        if (newData.length > 50) newData.shift();
        return { ...dataset, data: newData };
      });

      return { ...prevData, labels: newLabels, datasets: newDatasets };
    });

    const label = parseInt(currentRow[52], 10);
    if (videoMapping[label]) {
      setCurrentActivity(videoMapping[label].name);
      setVideoSrc(videoMapping[label].path);
    } else {
      setCurrentActivity("Unknown Activity");
      setVideoSrc("");
    }
  };

  return (
    <div className="min-h-screen bg-gray-50 p-8">
      <div className="max-w-7xl mx-auto">
        <div className="text-center mb-8">
          <h1 className="text-3xl font-bold text-gray-900 mb-4">
            Real-time Sensor Data Visualization
          </h1>
          <p className="text-gray-600 mb-6">
            Upload a CSV file to visualize sensor data and corresponding activities
          </p>
          <label className="inline-block">
            <span className="bg-blue-500 text-white px-6 py-3 rounded-lg cursor-pointer hover:bg-blue-600 transition-colors">
              Choose CSV File
              <input
                type="file"
                accept=".csv"
                onChange={handleFileUpload}
                className="hidden"
              />
            </span>
          </label>
        </div>

        <div className="grid grid-cols-1 lg:grid-cols-2 gap-8">
          {/* Graph Card */}
          <Card className="shadow-lg">
            <CardHeader>
              <CardTitle>Sensor Readings</CardTitle>
            </CardHeader>
            <CardContent className="h-[400px]">
              {chartData.labels.length > 0 ? (
                <Line
                  data={chartData}
                  options={{
                    responsive: true,
                    maintainAspectRatio: false,
                    scales: {
                      x: {
                        title: { 
                          display: true, 
                          text: "Time (samples)", 
                          font: { size: 14, weight: '500' } 
                        },
                        grid: { color: 'rgba(0,0,0,0.05)' },
                      },
                      y: {
                        title: { 
                          display: true, 
                          text: "Sensor Values", 
                          font: { size: 14, weight: '500' } 
                        },
                        grid: { color: 'rgba(0,0,0,0.05)' },
                        min: 0,
                        max: 20,
                      },
                    },
                    plugins: {
                      legend: { display: false },
                      tooltip: { 
                        mode: "index", 
                        intersect: false,
                        backgroundColor: 'rgba(0,0,0,0.8)',
                        padding: 12,
                        titleFont: { size: 14 },
                        bodyFont: { size: 13 }
                      },
                    },
                    animation: {
                      duration: 0
                    }
                  }}
                />
              ) : (
                <div className="h-full flex items-center justify-center text-gray-500">
                  Upload a CSV file to display the graph
                </div>
              )}
            </CardContent>
          </Card>

          {/* Video Card */}
          <Card className="shadow-lg">
            <CardHeader>
              <CardTitle className="flex justify-between items-center">
                <span>Activity Visualization</span>
                <span className="text-sm font-normal px-3 py-1 bg-blue-100 text-blue-800 rounded-full">
                  {currentActivity}
                </span>
              </CardTitle>
            </CardHeader>
            <CardContent>
              {videoSrc ? (
                <video
                  key={videoSrc}
                  className="w-full h-[400px] rounded-lg object-cover bg-gray-100"
                  loop
                  autoPlay
                  muted
                  controls={false}
                >
                  <source src={videoSrc} type="video/mp4" />
                  Your browser does not support video playback
                </video>
              ) : (
                <div className="h-[400px] flex items-center justify-center bg-gray-100 rounded-lg text-gray-500">
                  No video available for current activity
                </div>
              )}
            </CardContent>
          </Card>
        </div>
      </div>
    </div>
  );
};

export default CsvGraphPlotter;
