import React, { useState, useEffect, useCallback, useRef } from "react";
import Papa from "papaparse";
import { Line } from "react-chartjs-2";
import "chart.js/auto";

// Video Mapping
const videoMapping = {
  0: { name: "Sit", path: "/videos/sit.mp4" },
  1: { name: "Forward Bend", path: "/videos/forward_bend.mp4" },
  2: { name: "Warrior Pose", path: "/videos/warrior.mp4" },
  3: { name: "Triangle Pose", path: "/videos/triangle.mp4" },
  4: { name: "Vajrasana", path: "/videos/vajrasan.mp4" },
  5: { name: "Backward Bend", path: "/videos/backwardbend_pose.mp4" },
};

const CsvGraphPlotter = () => {
  const [data, setData] = useState([]); // Parsed CSV data
  const [currentIndex, setCurrentIndex] = useState(0); // Tracks the current starting row
  const [chartData, setChartData] = useState({ labels: [], datasets: [] }); // Graph data
  const [currentActivity, setCurrentActivity] = useState("Unknown Activity"); // Current activity label
  const [videoSrc, setVideoSrc] = useState(""); // Current video source

  const chartRef = useRef(null); // Ref for chart instance

  // Update video and graph incrementally like a sorting visualizer
  const updateChartAndVideo = useCallback((startIndex) => {
    if (startIndex >= data.length) return; // Stop if we are at the end of the data

    const row = data[startIndex]; // Get the next row of data
    const xLabels = [`t ${startIndex + 1}`]; // X-axis label for current data point

    // Create dataset for the graph - one dataset per column
    const datasets = Array.from({ length: 52 }, (_, colIdx) => ({
      label: `Column ${colIdx + 1}`,
      data: [parseFloat(row[colIdx])], // Add the value for the column at this point in time
      borderColor: `rgba(${Math.random() * 255}, ${Math.random() * 255}, ${Math.random() * 255}, 0.6)`, // Random color
      borderWidth: 1,
      fill: false,
    }));

    // Add new data point to existing chart
    setChartData((prevChartData) => {
      return {
        labels: [...prevChartData.labels, ...xLabels], // Add new label for X axis
        datasets: prevChartData.datasets.map((dataset, i) => ({
          ...dataset,
          data: [...dataset.data, datasets[i].data[0]], // Append the new data point to each dataset
        })),
      };
    });

    // Update video
    const label = parseInt(row[52], 10); // Extract the activity label from the last column
    if (videoMapping[label]) {
      setCurrentActivity(videoMapping[label].name);
      setVideoSrc(videoMapping[label].path);
    } else {
      setCurrentActivity("Unknown Activity");
      setVideoSrc("");
    }

    // Force update the chart if it's already rendered
    if (chartRef.current) {
      chartRef.current.chart.update();
    }
  }, [data]);

  // File upload handler
  const handleFileUpload = (event) => {
    const file = event.target.files[0];
    if (file) {
      Papa.parse(file, {
        complete: (result) => {
          const parsedData = result.data.filter((row) => row.length === 53); // Ensure valid rows with 53 columns
          setData(parsedData);
          setChartData({ labels: [], datasets: [] }); // Reset chart on new data upload
        },
        header: false,
        skipEmptyLines: true,
      });
    }
  };

  useEffect(() => {
    if (data.length === 0) return; // Do nothing if no data is available

    // Set up the interval to update the graph and video every second
    const interval = setInterval(() => {
      updateChartAndVideo(currentIndex);
      setCurrentIndex((prevIndex) => (prevIndex + 1) % data.length); // Increment and loop back at the end of the data
    }, 1000); // Update every 1 second (1000ms)

    return () => clearInterval(interval); // Cleanup when component unmounts or if data changes
  }, [data, currentIndex, updateChartAndVideo]); // Watch changes to data, currentIndex, and updateChartAndVideo

  return (
    <div style={{ textAlign: "center", padding: "20px" }}>
      <h1>CSV File Graph Plotter</h1>
      <input
        type="file"
        accept=".csv"
        onChange={handleFileUpload}
        style={{ margin: "20px", padding: "10px" }}
      />

      <div style={{ display: "flex", justifyContent: "space-around", padding: "20px" }}>
        {/* Graph Section */}
        <div style={{ width: "45%" }}>
          {chartData ? (
            <Line
              ref={chartRef} // Get the chart reference
              data={chartData}
              options={{
                responsive: true,
                maintainAspectRatio: false,
                scales: {
                  x: {
                    title: { display: true, text: "Time", font: { size: 14 } },
                    grid: { display: true },
                  },
                  y: {
                    title: { display: true, text: "Values", font: { size: 14 } },
                    grid: { display: true },
                    min: 0, // Y axis minimum value
                    max: 20, // Y axis maximum value
                    ticks: {
                      stepSize: 2, // Y axis tick spacing
                      callback: (value) => value.toFixed(2),
                    },
                  },
                },
                animation: {
                  duration: 0, // Disable chart animation on data load (can use animation for other visual effects)
                },
                plugins: {
                  legend: { display: false }, // No need for a legend
                  tooltip: { mode: "index", intersect: false },
                },
              }}
            />
          ) : (
            <p style={{ color: "#888" }}>Upload a CSV file to display the graph.</p>
          )}
        </div>

        {/* Video Section */}
        <div style={{ width: "45%" }}>
          <h1>Current Activity: {currentActivity}</h1>
          {videoSrc ? (
            <video
              key={videoSrc} // Force React to remount the video element when videoSrc changes
              width="100%"
              height="400"
              loop
              autoPlay
              muted
              controls={false}
              style={{
                objectFit: "contain",
                pointerEvents: "none",
              }}
            >
              <source src={videoSrc} type="video/mp4" />
              Your browser does not support the video tag.
            </video>
          ) : (
            <p>No video available for the current activity.</p>
          )}
        </div>
      </div>
    </div>
  );
};

export default CsvGraphPlotter;
