import React, { useState, useEffect } from "react";
import Papa from "papaparse";
import GraphBlock from "./components/GraphBlock";
import PoseInfoBlock from "./components/PoseInfoBlock";
import AnimationBlock from "./components/AnimationBlock";
import "./index.css";

const App = () => {
  const [csvData, setCsvData] = useState([]);
  const [currentChunk, setCurrentChunk] = useState([]);
  const [poseLabel, setPoseLabel] = useState(null);
  const [currentIndex, setCurrentIndex] = useState(0);

  // Labels for poses
  const poseMap = {
    0: "Sit",
    1: "Forward Bend",
    2: "Warrior Pose",
    3: "Triangle Pose",
    4: "Vajrasan",
    5: "Backward Bend",
  };

  // Handle file upload
  const handleFileUpload = (event) => {
    const file = event.target.files[0];

    Papa.parse(file, {
      header: false,
      skipEmptyLines: true,
      complete: (result) => {
        setCsvData(result.data);
      },
      error: (err) => {
        console.error("Error parsing CSV:", err);
      },
    });
  };

  // Update data in chunks of 10 rows
  useEffect(() => {
    if (csvData.length > 0) {
      const chunk = csvData.slice(currentIndex, currentIndex + 10);
      if (chunk.length === 10) {
        setCurrentChunk(chunk);
        setPoseLabel(chunk[9][52]); // Last column of the 10th row
      }
    }
  }, [currentIndex, csvData]);

  // Loop over data in 10-row chunks
  useEffect(() => {
    if (csvData.length > 0) {
      const interval = setInterval(() => {
        setCurrentIndex((prev) => (prev + 10 >= csvData.length ? 0 : prev + 10));
      }, 5000); // 5 seconds delay for each iteration

      return () => clearInterval(interval);
    }
  }, [csvData]);

  return (
    <div className="App">
      <h1>Yoga Activity Viewer</h1>
      <input type="file" accept=".csv" onChange={handleFileUpload} />

      <div className="blocks">
        {currentChunk.length > 0 && <GraphBlock data={currentChunk} />}
        <PoseInfoBlock poseLabel={poseLabel} poseMap={poseMap} />
        <AnimationBlock poseLabel={poseLabel} />
      </div>
    </div>
  );
};

export default App;
