import React, { useState, useEffect } from "react";
import Papa from "papaparse";
import { Line } from "react-chartjs-2";
import "chart.js/auto"; // Automatically imports the required chart components

const CsvGraphPlotter = () => {
  const [data, setData] = useState([]); // Stores the parsed data from the CSV
  const [currentIndex, setCurrentIndex] = useState(0); // Tracks the starting row for plotting
  const [chartData, setChartData] = useState({}); // Stores the data for the graph

  // Automatically update the graph every second based on data
  useEffect(() => {
    if (data.length > 0) {
      const interval = setInterval(() => {
        updateChart(currentIndex);
        setCurrentIndex((prevIndex) => (prevIndex + 50) % data.length);
      }, 1000); // Change graph every 1 second
      return () => clearInterval(interval); // Cleanup interval
    }
  }, [data, currentIndex]);

  // Handle CSV file upload and parsing
  const handleFileUpload = (event) => {
    const file = event.target.files[0]; // Get the uploaded file
    if (file) {
      Papa.parse(file, {
        complete: (result) => {
          const parsedData = result.data.filter((row) => row.length === 53); // Ensure all rows have 53 columns
          setData(parsedData); // Store the parsed data
        },
        header: false,
        skipEmptyLines: true, // Skip any empty rows
      });
    }
  };

  // Update the chart for the current slice of data (50 rows)
  const updateChart = (index) => {
    const rows = data.slice(index, index + 50); // Select the next 50 rows
    const labels = Array.from({ length: 52 }, (_, i) => `Column ${i + 1}`); // Generate labels for columns
    const datasets = rows.map((row, i) => ({
      label: `Row ${index + i + 1}`,
      data: row.slice(0, 52).map(Number), // Convert string data to numbers
      fill: false,
      borderColor: `rgba(${Math.random() * 255}, ${Math.random() * 255}, ${Math.random() * 255}, 0.8)`, // Random color
    }));

    setChartData({ labels, datasets }); // Update chart data state
  };

  return (
    <div style={{ textAlign: "center", padding: "20px" }}>
      <h1>CSV File Graph Plotter</h1>
      {/* File upload button */}
      <input
        type="file"
        accept=".csv"
        onChange={handleFileUpload}
        style={{ margin: "20px 0", padding: "10px" }}
      />
      {chartData.labels ? (
        <div style={{ margin: "20px auto", width: "90%" }}>
          <Line data={chartData} options={{ responsive: true, maintainAspectRatio: false }} />
        </div>
      ) : (
        <p style={{ color: "#888" }}>Upload a CSV file to display the graph.</p>
      )}
    </div>
  );
};

export default CsvGraphPlotter;
