import React, { useEffect, useState } from "react";
import Papa from "papaparse";
import Graph from "./components/Graph";
import VideoPlayer from "./components/VideoPlayer";

const App = () => {
  const [data, setData] = useState([]);
  const [poseLabel, setPoseLabel] = useState(0);
  const [rowIndex, setRowIndex] = useState(0);

  const readCSV = () => {
    Papa.parse("/path-to-your-file.csv", {
      download: true,
      complete: (result) => {
        setData(result.data);
      }
    });
  };

  useEffect(() => {
    readCSV();
  }, []);

  useEffect(() => {
    const interval = setInterval(() => {
      if (rowIndex + 9 < data.length) {
        const subset = data.slice(rowIndex, rowIndex + 10);
        setPoseLabel(subset[9][52]); // Update pose label from the last column
        setRowIndex(rowIndex + 10);
      } else {
        setRowIndex(0); // Reset to the beginning when done
      }
    }, 5000); // Update every 5 seconds (or customize based on needs)

    return () => clearInterval(interval);
  }, [rowIndex, data]);

  const currentData = data.slice(rowIndex, rowIndex + 10);

  return (
    <div className="App">
      <h1>Sensorless Sensing </h1>

      <div className="graph">
        <h2>Graph of Amplitude Values (10 rows)</h2>
        <Graph data={currentData.map(row => row.slice(0, 52))} />
      </div>

      <div className="video-player">
        <h2>Yoga Pose: {["Sit", "Forward Bend", "Warrior Pose", "Triangle Pose", "Vajrasan", "Backward Bend"][poseLabel]}</h2>
        <VideoPlayer poseLabel={poseLabel} />
      </div>
    </div>
  );
};

export default App;
