import React, { useState } from 'react';
import Papa from 'papaparse'; // CSV parser
import GraphBlock from './GraphBlock';
import PoseBlock from './PoseBlock';
import VideoBlock from './VideoBlock_temp';
import './index.css'; // Import CSS styles

const App = () => {
  const [csvData, setCsvData] = useState([]);
  const [currentPose, setCurrentPose] = useState(null);

  // Pose mappings for name and video
  const poseMapping = {
    1: { name: 'Forward Bend Pose', video: '/videos/forward_bend.mp4' },
    2: { name: 'Warrior Pose', video: '/videos/warrior.mp4' },
    3: { name: 'Triangle Pose', video: '/videos/triangle.mp4' },
    4: { name: 'Vajrasan', video: '/videos/vajrasan.mp4' },
    5: { name: 'Backward Bend Pose', video: '/videos/backward_bend.mp4' },
  };

  // Handle file upload
  const handleFileUpload = (event) => {
    const file = event.target.files[0];
    if (file) {
      Papa.parse(file, {
        header: true, // CSV has headers
        skipEmptyLines: true,
        dynamicTyping: true,
        complete: (results) => {
          console.log('Parsed CSV Data:', results.data);
          setCsvData(results.data);
        },
        error: (error) => {
          console.error('Error parsing CSV:', error);
        },
      });
    }
  };

  // Handle pose selection based on index
  const handlePoseSelection = (index) => {
    const row = csvData[index];
    if (row) {
      const poseId = row['Pose ID']; // Access "Pose ID" column explicitly
      if (poseId) {
        setCurrentPose({
          signalData: { ...row, 'Pose ID': undefined }, // Remove Pose ID from graph data
          name: poseMapping[poseId]?.name || 'Unknown Pose',
          video: poseMapping[poseId]?.video || '',
        });
      } else {
        console.error('Pose ID missing in selected row:', row);
      }
    } else {
      console.error('Row not found at index:', index);
    }
  };

  return (
    <div className="app-container">
      <h1 className="title">Upload here</h1>
      <button className="upload-button">
        <input
          type="file"
          accept=".csv"
          onChange={handleFileUpload}
          className="upload-input"
        />
        Upload CSV File
      </button>
      {csvData.length > 0 && (
        <button
          className="action-button"
          onClick={() => handlePoseSelection(0)} // Default to the first row
        >
          Show First Pose
        </button>
      )}
      {currentPose && (
        <div className="content">
          <GraphBlock data={currentPose.signalData} />
          <PoseBlock poseName={currentPose.name} />
          <VideoBlock videoUrl={currentPose.video} />
        </div>
      )}
    </div>
  );
};

export default App;
