import React from 'react';
import { Line } from 'react-chartjs-2';

const GraphBlock = ({ data }) => {
  const keys = Object.keys(data).filter((key) => key.startsWith('Time')); // Ensure only time series
  const values = keys.map((key) => data[key]);

  const chartData = {
    labels: keys,
    datasets: [
      {
        label: 'Signal Data',
        data: values,
        borderColor: 'blue',
        backgroundColor: 'rgba(135,206,250,0.5)',
      },
    ],
  };

  const options = {
    responsive: true,
    scales: {
      x: { title: { display: true, text: 'Time' } },
      y: { title: { display: true, text: 'Signal Value' } },
    },
  };

  return (
    <div className="block graph-block">
      <h2>Signal Graph</h2>
      <Line data={chartData} options={options} />
    </div>
  );
};

export default GraphBlock;
