import React from "react";
import { Player } from "@lottiefiles/react-lottie-player";
import sitAnimation from "../animations/sit.json"; // Replace with correct paths
import forwardBendAnimation from "../animations/forward_bend.json";
import warriorAnimation from "../animations/warrior.json";
import triangleAnimation from "../animations/triangle.json";
import vajrasanAnimation from "../animations/vajrasan.json";
import backwardBendAnimation from "../animations/backward_bend.json";

const AnimationBlock = ({ poseLabel }) => {
  const animations = {
    0: sitAnimation,
    1: forwardBendAnimation,
    2: warriorAnimation,
    3: triangleAnimation,
    4: vajrasanAnimation,
    5: backwardBendAnimation,
  };

  return (
    <div className="animation-block">
      <h2>Pose Animation</h2>
      {poseLabel !== null && animations[poseLabel] ? (
        <Player autoplay loop src={animations[poseLabel]} style={{ height: "300px", width: "300px" }} />
      ) : (
        <p>No Animation Available</p>
      )}
    </div>
  );
};

export default AnimationBlock;
