import React, { useState, useEffect } from "react";
import Papa from "papaparse";
import { Line } from "react-chartjs-2";
import "chart.js/auto";

const CsvGraphPlotter = () => {
  const [data, setData] = useState([]); // Stores parsed CSV data
  const [currentIndex, setCurrentIndex] = useState(0); // Tracks the current starting row
  const [chartData, setChartData] = useState(null); // Stores data for graph plotting
  const [activity, setActivity] = useState(""); // Stores the activity name
  const [videoSrc, setVideoSrc] = useState(""); // Stores the video path

  // Map activity labels to activity names and video paths
  const activityMap = {
    0: { name: "Sit", video: "/videos/sit.mp4" },
    1: { name: "Forward Bend", video: "/videos/forward_bend.mp4" },
    2: { name: "Warrior Pose", video: "/videos/warrior_pose.mp4" },
    3: { name: "Triangle Pose", video: "/videos/triangle_pose.mp4" },
    4: { name: "Vajrasan", video: "/videos/vajrasan.mp4" },
    5: { name: "Backward Bend", video: "/videos/backward_bend.mp4" },
  };
  

  // Automatically update the graph and activity every second
  useEffect(() => {
    if (data.length > 0) {
      const interval = setInterval(() => {
        updateChart(currentIndex);
        setCurrentIndex((prevIndex) => (prevIndex + 50) % data.length); // Loop through all rows
      }, 1000); // Update every 1 second
      return () => clearInterval(interval); // Clear interval when component unmounts
    }
  }, [data, currentIndex]);

  // Handle CSV file upload
  const handleFileUpload = (event) => {
    const file = event.target.files[0];
    if (file) {
      Papa.parse(file, {
        complete: (result) => {
          // Log the parsed CSV data
          console.log("Parsed CSV Data:", result.data);
  
          // Ensure rows have exactly 53 columns, filter out any rows that do not match
          const parsedData = result.data.filter(row => row.length === 53); 
          console.log("Valid Rows:", parsedData); // Log only the valid rows
  
          setData(parsedData); // Save parsed data
          if (parsedData.length > 0) {
            updateChart(0); // Start charting with the first 50 rows
          } else {
            console.error("No valid rows with 53 columns.");
          }
        },
        header: false,
        skipEmptyLines: true,
      });
    }
  };
  

  // Update chart for the current chunk of rows
  const updateChart = (startIndex) => {
    const endIndex = startIndex + 50 > data.length ? data.length : startIndex + 50;
    const rows = data.slice(startIndex, endIndex); // Get the next chunk of rows
  
    if (rows.length === 0) {
      console.error("Error: No rows to process");
      return;
    }
  
    const xLabels = rows.map((_, idx) => `Row ${startIndex + idx + 1}`); // Labels for rows (x-axis)
  
    // Create datasets for each column
    const datasets = Array.from({ length: 52 }, (_, colIdx) => ({
      label: `Column ${colIdx + 1}`,
      data: rows.map((row) => parseFloat(row[colIdx])), // Use column values for y-axis
      borderColor: `rgba(${Math.random() * 255}, ${Math.random() * 255}, ${Math.random() * 255}, 0.6)`, // Random colors
      borderWidth: 1,
      fill: false,
    }));
  
    setChartData({
      labels: xLabels, // Set x-axis labels
      datasets,
    });
  
    // Check if the first row exists and safely access the 53rd column (index 52)
    const firstRow = rows[0];
    if (firstRow && firstRow.length > 52) {
      const label = firstRow[52]; // First row, 53rd column (index 52)
  
      if (activityMap[label]) {
        setActivity(activityMap[label].name); // Set the corresponding activity name
        setVideoSrc(activityMap[label].video); // Set the corresponding video
      }
    } else {
      console.error("Error: First row doesn't contain a valid label in 53rd column.");
      setActivity("Unknown Activity"); // Default activity
      setVideoSrc(""); // No video to show
    }
  };
  

  return (
    <div style={{ textAlign: "center", padding: "20px" }}>
      <h1>CSV File Graph Plotter</h1>
      {/* File Upload */}
      <input
        type="file"
        accept=".csv"
        onChange={handleFileUpload}
        style={{ margin: "20px", padding: "10px" }}
      />

      {/* First block: Graph */}
      <div style={{ margin: "20px auto", width: "80%", height: "500px" }}>
        {chartData ? (
          <Line
            data={chartData}
            options={{
              responsive: true,
              maintainAspectRatio: false,
              scales: {
                x: {
                  title: { display: true, text: "Rows", font: { size: 14 } },
                  grid: { display: true },
                },
                y: {
                  title: { display: true, text: "Values", font: { size: 14 } },
                  grid: { display: true },
                  min: 0, // Set minimum Y value to 0
                  max: 20, // Limit Y axis to a maximum value of 20
                  ticks: {
                    stepSize: 2, // Step size to control spacing between ticks (optional)
                    callback: (value) => value.toFixed(2), // Optionally format the y-axis tick labels
                  },
                },
              },
              plugins: {
                legend: { display: true, position: "right" },
                tooltip: { mode: "index", intersect: false },
              },
            }}
          />
        ) : (
          <p style={{ color: "#888" }}>Upload a CSV file to display the graph.</p>
        )}
      </div>

      {/* Second block: Activity Name */}
      <div style={{ margin: "20px" }}>
        <h2>Activity: {activity}</h2>
      </div>

      {/* Third block: Video */}
      <div style={{ margin: "20px" }}>
        {videoSrc && (
          <video width="80%" height="auto" controls>
            <source src={videoSrc} type="video/mp4" />
            Your browser does not support the video tag.
          </video>
        )}
      </div>
    </div>
  );
};

export default CsvGraphPlotter;
