import React from "react";
import ReactPlayer from "react-player";

const VideoPlayer = ({ poseLabel }) => {
  const getVideoPath = (poseLabel) => {
    switch (poseLabel) {
      case 0: return "/videos/sit.mp4";
      case 1: return "/videos/forward-bend.mp4";
      case 2: return "/videos/warrior-pose.mp4";
      case 3: return "/videos/triangle-pose.mp4";
      case 4: return "/videos/vajrasan.mp4";
      case 5: return "/videos/backward-bend.mp4";
      default: return "/videos/sit.mp4"; // Default video
    }
  };

  return (
    <ReactPlayer
      url={getVideoPath(poseLabel)}
      controls
      width="100%"
      height="auto"
    />
  );
};

export default VideoPlayer;
