import React, { useState, useEffect } from "react";
import Papa from "papaparse";
import { Line } from "react-chartjs-2";
import "chart.js/auto";

const videoMapping = {
  0: { name: "Sit", path: "/videos/sit.mp4" },
  1: { name: "Forward Bend", path: "/videos/forward_bend.mp4" },
  2: { name: "Warrior Pose", path: "/videos/warrior.mp4" },
  3: { name: "Triangle Pose", path: "/videos/triangle.mp4" },
  4: { name: "Vajrasana", path: "/videos/vajrasan.mp4" },
  5: { name: "Backward Bend", path: "/videos/backwardbend_pose.mp4" },
};

const CsvGraphPlotter = () => {
  const [data, setData] = useState([]); // Parsed CSV data
  const [currentIndex, setCurrentIndex] = useState(0); // Tracks the current starting row
  const [chartData, setChartData] = useState(null); // Graph data
  const [currentActivity, setCurrentActivity] = useState("Unknown Activity"); // Current activity label
  const [videoSrc, setVideoSrc] = useState(""); // Current video source

  const chartRef = React.useRef(); // Ref for accessing chart instance

  // Update video and graph incrementally like a sorting visualizer
  useEffect(() => {
    if (data.length > 0) {
      const interval = setInterval(() => {
        // Fetch the next row
        updateChartAndVideo(currentIndex);
        setCurrentIndex((prevIndex) => (prevIndex + 1) % data.length); // Loop through rows

      }, 50); // Speed of update (50ms = smooth visual transitions)

      return () => clearInterval(interval); // Cleanup on unmount
    }
  }, [data, currentIndex]);

  // File upload handler
  const handleFileUpload = (event) => {
    const file = event.target.files[0];
    if (file) {
      Papa.parse(file, {
        complete: (result) => {
          const parsedData = result.data.filter((row) => row.length === 53); // Ensure rows with exactly 53 columns
          setData(parsedData);
          updateChartAndVideo(0); // Initialize chart and video
        },
        header: false,
        skipEmptyLines: true,
      });
    }
  };

  // Function to update the chart data dynamically like a sorting visualizer
  const updateChartAndVideo = (startIndex) => {
    const row = data[startIndex]; // Get the next row
    const xLabels = [`t ${startIndex + 1}`]; // X-axis label based on the row

    // Create datasets for the graph
    const datasets = Array.from({ length: 52 }, (_, colIdx) => ({
      label: `Column ${colIdx + 1}`,
      data: [parseFloat(row[colIdx])], // Take only one data point per update
      borderColor: `rgba(${Math.random() * 255}, ${Math.random() * 255}, ${Math.random() * 255}, 0.6)`,
      borderWidth: 1,
      fill: false,
    }));

    setChartData((prevChartData) => ({
      ...prevChartData,
      labels: [...prevChartData?.labels || [], ...xLabels], // Append X labels
      datasets: prevChartData
        ? prevChartData.datasets.map((dataset, i) => ({
            ...dataset,
            data: [...dataset.data, datasets[i].data[0]], // Incrementally append each data point
          }))
        : datasets, // Initialize dataset when first created
    }));

    // Handle video update based on the label
    const label = parseInt(row[52], 10); // Get the label from the last column
    if (videoMapping[label]) {
      setCurrentActivity(videoMapping[label].name);
      setVideoSrc(videoMapping[label].path);
    } else {
      setCurrentActivity("Unknown Activity");
      setVideoSrc("");
    }

    // Trigger chart updates with animation (using chartjs ref)
    if (chartRef.current) {
      chartRef.current.chartInstance.update(); // Update chart with new data smoothly
    }
  };

  return (
    <div style={{ textAlign: "center", padding: "20px" }}>
      <h1>CSV File Graph Plotter</h1>
      <input
        type="file"
        accept=".csv"
        onChange={handleFileUpload}
        style={{ margin: "20px", padding: "10px" }}
      />

      <div style={{ display: "flex", justifyContent: "space-around", padding: "20px" }}>
        {/* Graph Section */}
        <div style={{ width: "45%" }}>
          {chartData ? (
            <Line
              ref={chartRef} // Get the chart instance to trigger updates
              data={chartData}
              options={{
                responsive: true,
                maintainAspectRatio: false,
                scales: {
                  x: {
                    title: { display: true, text: "Time", font: { size: 14 } },
                    grid: { display: true },
                  },
                  y: {
                    title: { display: true, text: "Values", font: { size: 14 } },
                    grid: { display: true },
                    min: 0, // Set minimum Y value to 0
                    max: 20, // Limit Y axis to a maximum value of 20
                    ticks: {
                      stepSize: 2, // Step size to control spacing between ticks (optional)
                      callback: (value) => value.toFixed(2), // Optionally format the y-axis tick labels
                    },
                  },
                },
                animation: {
                  duration: 0, // No animation on initial load
                },
                plugins: {
                  legend: { display: false },
                  tooltip: { mode: "index", intersect: false },
                },
              }}
            />
          ) : (
            <p style={{ color: "#888" }}>Upload a CSV file to display the graph.</p>
          )}
        </div>

        {/* Video Section */}
        <div style={{ width: "45%" }}>
          <h1>Current Activity: {currentActivity}</h1>
          {videoSrc ? (
            <video
              key={videoSrc} // Force React to remount the video element when videoSrc changes
              width="100%"
              height="400"
              loop
              autoPlay
              muted
              controls={false}
              style={{
                objectFit: "contain",
                pointerEvents: "none",
              }}
            >
              <source src={videoSrc} type="video/mp4" />
              Your browser does not support the video tag.
            </video>
          ) : (
            <p>No video available for the current activity.</p>
          )}
        </div>
      </div>
    </div>
  );
};

export default CsvGraphPlotter;
